#include <stdio.h>
#include <math.h>
int divisors_found(unsigned long long int num){
    int found;
    found = 2;
    int lcv;
    for (lcv = 2; lcv < (floor(sqrt(num))) + 1; lcv += 1){
        if (num % lcv == 0) found += 2;
    };
    if (found > 100) printf("[n: %llu, d: %d] ", num, found);
    return found;
};
int main(){
    unsigned long long int current_triangle;
    int lcv;
    current_triangle = 10ULL;
    lcv = 5;
    while (divisors_found(current_triangle) < 500){
        current_triangle += lcv;
        lcv += 1;
    };
    printf("\n\ncurrent_triangle is: %llu\nDone.\n", current_triangle);
}

